mapper.select:
SELECT a.qr_id AS qrId, a.user_no AS userNo, a.check_time AS checkTime, u.no AS userNo, u.id AS userId, u.name AS userName, u.email AS userEmail, u.phone AS userPhone FROM attendance a INNER JOIN users u ON a.user_no = u.no
AND u.name LIKE CONCAT('%', #{option.keyword}, '%')
AND a.some_column = #{option.code}
ORDER BY a.check_time DESC LIMIT #{page.index}, #{page.rows}

SELECT COUNT(*) FROM attendance a INNER JOIN users u ON a.user_no = u.no
AND u.name LIKE CONCAT('%', #{option.keyword}, '%')
AND a.some_column = #{option.code}

SELECT COUNT(*) 
FROM attendance 
WHERE user_no = #{userNo}
